import React, { useState, useEffect } from 'react';
import playerPlaneImage from './images/player-plane.png';
import enemyPlaneImage from './images/enemy-plane.png';
import bulletImage from './images/bullet.png';
import enemyBulletImage from './images/enemy-bullet.png';

export default function App() {
  const [player, setPlayer] = useState({
    x: 700,
    y: 200,
    health: 5,
    ammo: 10,
    display: playerPlaneImage,
  });

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  const handleKeyDown = (event) => {
    if (event.key === 'ArrowLeft') {
      movePlayerLeft();
    } else if (event.key === 'ArrowRight') {
      movePlayerRight();
    }
  };

  const movePlayerLeft = () => {
    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      x: prevPlayer.x - 10,
    }));
  };

  const movePlayerRight = () => {
    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      x: prevPlayer.x + 10,
    }));
  };

  const style = {
    position: 'relative',
    height: 110,
    width: 100,
    right:player.x, // Adjust the position based on player's x coordinate
    top:player.y, // Adjust the position based on player's y coordinate
  };

  return (
    <img src={player.display} alt="Player Plane" style={style} />
  );
}
