import React, { useState, useEffect } from 'react';
import playerPlaneImage from './images/player-plane.png';
import enemyImage from './images/enemy.png';

const App = () => {
  const [playerPlane, setPlayerPlane] = useState({ x: 0, y: 0, ammo: 5, health: 100 });
  const [enemies, setEnemies] = useState([]);
  const [score, setScore] = useState(0);

  // Function to handle player movement
  const handlePlayerMovement = (event) => {
    const { key } = event;
    const speed = 10;

    // Move player plane based on arrow keys
    if (key === 'ArrowUp') {
      setPlayerPlane((prevPlane) => ({ ...prevPlane, y: prevPlane.y - speed }));
    } else if (key === 'ArrowDown') {
      setPlayerPlane((prevPlane) => ({ ...prevPlane, y: prevPlane.y + speed }));
    } else if (key === 'ArrowLeft') {
      setPlayerPlane((prevPlane) => ({ ...prevPlane, x: prevPlane.x - speed }));
    } else if (key === 'ArrowRight') {
      setPlayerPlane((prevPlane) => ({ ...prevPlane, x: prevPlane.x + speed }));
    }
  };

  // Function to handle player shooting
  const handlePlayerShoot = () => {
    if (playerPlane.ammo > 0) {
      // Create a new bullet object
      const bullet = { x: playerPlane.x, y: playerPlane.y };
      setPlayerPlane((prevPlane) => ({ ...prevPlane, ammo: prevPlane.ammo - 1 }));

      // Add the bullet to the enemy array
      setEnemies((prevEnemies) => [...prevEnemies, bullet]);
    }
  };

  // Function to check collision between bullets and enemies
  const checkCollision = () => {
    const updatedEnemies = [...enemies];

    updatedEnemies.forEach((enemy, enemyIndex) => {
      if (enemy.x === playerPlane.x && enemy.y === playerPlane.y) {
        // Player collided with enemy
        setPlayerPlane((prevPlane) => ({ ...prevPlane, health: prevPlane.health - 10 }));

        if (playerPlane.health <= 0) {
          // Game over condition
          alert('Game over!');
          resetGame();
        }
      }

      updatedEnemies.forEach((bullet, bulletIndex) => {
        if (bullet.x === enemy.x && bullet.y === enemy.y) {
          // Bullet collided with enemy
          updatedEnemies.splice(enemyIndex, 1);
          setScore((prevScore) => prevScore + 10);
        }
      });
    });

    setEnemies(updatedEnemies);
  };

  // Function to reset the game
  const resetGame = () => {
    setPlayerPlane({ x: 0, y: 0, ammo: 5, health: 100 });
    setEnemies([]);
    setScore(0);
  };

  useEffect(() => {
    // Event listener for player movement
    window.addEventListener('keydown', handlePlayerMovement);

    // Interval for enemy movement and collision detection
    const gameInterval = setInterval(() => {
      setEnemies((prevEnemies) =>
        prevEnemies.map((enemy) => ({
          ...enemy,
          x: enemy.x - 5, // Adjust enemy speed here
        }))
      );

      checkCollision();
    }, 100);

    return () => {
      window.removeEventListener('keydown', handlePlayerMovement);
      clearInterval(gameInterval);
    };
  }, []);

  return (
    <div>
      <h1>Shooter Game</h1>
      <div>
        <p>Player Ammo: {playerPlane.ammo}</p>
        <p>Player Health: {playerPlane.health}</p>
        <p>Score: {score}</p>
      </div>
      <div
        style={{
          position: 'relative',
          width: '500px',
          height: '500px',
          border: '1px solid black',
        }}
      >
        <img
          src={playerPlaneImage}
          alt="Player Plane"
          style={{
            position: 'absolute',
            top: playerPlane.y + 'px',
            left: playerPlane.x + 'px',
            width: '50px',
            height: '50px',
          }}
        />
        {enemies.map((enemy, index) => (
          <img
            key={index}
            src={enemyImage}
            alt="Enemy"
            style={{
              position: 'absolute',
              top: enemy.y + 'px',
              left: enemy.x + 'px',
              width: '30px',
              height: '30px',
            }}
          />
        ))}
      </div>
      <button onClick={handlePlayerShoot}>Shoot</button>
      <button onClick={resetGame}>Reset</button>
    </div>
  );
};

export default App;
