import React, { useState, useEffect } from 'react';
import playerPlaneImage from './images/player-plane.png';
import enemyPlaneImage from './images/enemy-plane.png';
import bulletImage from './images/bullet.png';
import enemyBulletImage from './images/enemy-bullet.png';

export default function App() {
  const [player, setPlayer] = useState({
    x: 700,
    y: 650,
    health: 5,
    ammo: 10,
    display: playerPlaneImage,
  });
  const [bullet, setBullet] = useState(null);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  const handleKeyDown = (event) => {
    if (event.key === 'ArrowLeft') {
      movePlayerLeft();
    } else if (event.key === 'ArrowRight') {
      movePlayerRight();
    } else if (event.key === ' ') {
      putBullet();
    }
  };

  const putBullet = () => {
    setBullet({
      x: player.x + 20,
      y: player.y - 50,
      display: bulletImage,
    });
  };

  useEffect(() => {
    if (bullet && bullet.y >= 0) {
      const bulletInterval = setInterval(() => {
        setBullet((prevBullet) => ({
          ...prevBullet,
          y: prevBullet.y - 10,
        }));
      }, 100);
      return () => clearInterval(bulletInterval);
    }
  }, [bullet]);

  const movePlayerLeft = () => {
    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      x: prevPlayer.x - 10,
    }));
  };

  const movePlayerRight = () => {
    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      x: prevPlayer.x + 10,
    }));
  };

  const style = {
    position: 'relative',
    height: 110,
    width: 100,
    left: player.x,
    top: player.y,
  };

  return (
    <div>
      <img src={player.display} alt="Player Plane" style={style} />
      {bullet && (
        <img
          src={bullet.display}
          alt="Bullet"
          style={{
            position: 'absolute',
            left: bullet.x,
            top: bullet.y,
            height: 40,
            width: 50,
          }}
        />
      )}
    </div>
  );
}
