import React, { useState, useEffect } from 'react';
import playerPlaneImage from './images/player-plane.png';

export default function App() {
  const [player, setPlayer] = useState({
    x: 700,
    y: 650,
    health: 5,
    ammo: 10,
    display: playerPlaneImage,
  });

  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  const handleKeyDown = (event) => {
    if (event.key === 'ArrowLeft') {
      movePlayerLeft();
    } else if (event.key === 'ArrowRight') {
      movePlayerRight();
    } else if (event.key === ' ') {
      shoot();
    }
  };

  const movePlayerLeft = () => {
    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      x: prevPlayer.x - 10,
    }));
  };

  const movePlayerRight = () => {
    setPlayer((prevPlayer) => ({
      ...prevPlayer,
      x: prevPlayer.x + 10,
    }));
  };

  const shoot = () => {
    // Add your shooting logic here
    alert('Shoot!');
  };

  const style = {
    position: 'absolute',
    height: 110,
    width: 100,
    transform: player.x,
    top: player.y, // Adjust the position based on player's y coordinate
  };

  return <img src={player.display} alt="Player Plane" style={style} />;
}
